INSERT INTO t_skills (created_at,last_updated_at, deleted_at, is_available, name, descriptions)
VALUES
('1698330600000', '1698330600000', NULL, 1, '奇门遁甲', '上古三式之一，奇门遁甲是中国古代术数体系，综合了天文、地理、兵法等多方面知识，用于预测和决策。'),
('1698330600000', '1698330600000', NULL, 1, '大六壬', '上古三式之一，大六壬以占卜人事著称，通过课式来推断事物发展变化。'),
('1698330600000', '1698330600000', NULL, 1, '太乙神数', '上古三式之一，太乙神数是古代高层次预测学，被称为帝王之术，用于预测国家政治、军事等大事。'),
('1698330600000', '1698330600000', NULL, 1, '四柱八字', '四柱八字以人出生的年、月、日、时为基础，通过天干地支组合成八字，来推算人的命运。'),
('1698330600000', '1698330600000', NULL, 1, '禄命法', '禄命法以人的出生年、月、日所值干支来推算人的禄命，判断人的贵贱贫富、寿夭祸福。'),
('1698330600000', '1698330600000', NULL, 1, '紫微斗数', '紫微斗数是一种星命术，以人的出生年、月、日、时确定十二宫的位置，结合星曜来预测人生运势。'),
('1698330600000', '1698330600000', NULL, 1, '七政四余', '七政四余是中国古代占星学，通过对日月和五大行星以及罗睺、计都、紫炁、月孛的运行规律来推断人的命运。'),
('1698330600000', '1698330600000', NULL, 1, '演禽术', '演禽术是一种以十二种动物命名神煞，通过它们的生克关系来预测人事吉凶的术数。'),
('1698330600000', '1698330600000', NULL, 1, '梅花易数', '梅花易数是一种简易的占卜方法，通过随机事物起卦，以《易经》为基础进行占断。'),
('1698330600000', '1698330600000', NULL, 1, '六爻', '六爻是利用三枚铜钱摇卦，根据所得卦象来预测事物发展变化的占卜方法。'),
('1698330600000', '1698330600000', NULL, 1, '小六壬', '小六壬是一种简便的占卜方式，通过月、日、时的组合来快速判断事情的吉凶。'),
('1698330600000', '1698330600000', NULL, 1, '周易', '《周易》是中国传统思想文化中自然哲学与人文实践的理论根源，可用于哲学思考、占卜预测等。'),
('1698330600000', '1698330600000', NULL, 1, '太玄经', '《太玄经》是模仿《周易》而作的一部哲学著作，也可用于占卜等方面。'),
('1698330600000', '1698330600000', NULL, 1, '京房易', '京房易是西汉京房所创的易学流派，将《周易》与阴阳五行相结合，用于占卜和预测。'),
('1698330600000', '1698330600000', NULL, 1, '铁板神数', '铁板神数是一种以生辰八字为基础，结合特定规则来推算人的命运、六亲等信息的术数。'),
('1698330600000', '1698330600000', NULL, 1, '邵子神数', '邵子神数是北宋邵雍所创的一种术数，以河洛数理为基础，用于预测人生的各种信息。'),
('1698330600000', '1698330600000', NULL, 1, '皇极数', '皇极数是一种高级的数术，与《皇极经世书》相关，可用于预测和推演事物发展。'),
('1698330600000', '1698330600000', NULL, 1, '形势派', '形势在风水等领域指山川地理的外在形态，通过观察形势来判断环境的吉凶。'),
('1698330600000', '1698330600000', NULL, 1, '理气派', '理气是风水理论的重要组成部分，通过阴阳五行、八卦等理论来分析和调整气场。');
INSERT INTO t_skill_classes (uuid,created_at, last_updated_at, deleted_at,skill_id, is_customized, name,feature,specification)
VALUES
('d6822577-8334-4449-b3ac-40f6a30a6dfa','1698330600000', '1698330600000', NULL, 7, 0,'果老派','以政余星五行属性以及之间相互关系为主；度主为论短核心；辅助神煞系统论断（独创国印、唐符）。', '相传由唐代道士张果老创立，以《果老星宗》为经典文献，是目前传播最广的流派。此派以度主论命为核心，即通过观察七政四余星曜在二十八星宿的度数及十二宫庙旺状态，结合五行生克、经络贯串等技法推断命运，化曜系统使用八字十神。其理论体系强调实际天体运行轨迹（如日月五星的迟留伏逆），并融入唐代《符天历》的星算历法'),
('1bea8709-60f5-4a62-bea4-e6e16a9c4a0d','1698330600000', '1698330600000', NULL, 7, 0,'天官派','独创十一曜化曜（禄科权）；重视星曜格局；强调“宫主为体，度主为用”；精确推运，细分流年时间', '据传源自元代耶律楚材，清代由舒继英、廖翼亨等人完善，是七政四余的集大成者。此派以官星格局为核心，结合天官化曜系统（如禄勋、科名）及经络贯串技法，注重人生格局的层次与起伏'),
('17813823-7e6b-4aab-97cf-a3aff5b16656','1698330600000', '1698330600000', NULL, 7, 0,'琴堂派','与四柱八字融合；大量使用神煞及天盘加临，地盘通关，弱化实际星体运行。', '最古老的七政四余流派，传说由唐代僧人一行创立。此派以宫主论命为核心，结合生辰八字与太岁神煞（如天德、月德），强调“逢生坐实、占高强”的格局判断。'),
('eb5f1810-a0b1-4fb4-b3fa-4f3f917e6435','1698330600000', '1698330600000', NULL, 1, 0,'年家奇门','阴遁专用，六十年周期；重年干支	国运、自然灾害预测', ''),
('fec0941f-e4e4-477b-95b5-011152dcda0c','1698330600000', '1698330600000', NULL, 1, 0,'月家奇门','五年周期，四孟/仲/季分三元；中长期项目规划', ''),
('7b3425e5-ebaf-43a6-83c1-16ba4c1bb020','1698330600000', '1698330600000', NULL, 1, 0,'日家奇门','每日换局，节气分阴阳遁；日常事务决策，出行择吉等', ''),
('d810c962-f5df-4dfd-bc14-211feb285bfb','1698330600000', '1698330600000', NULL, 1, 0,'转盘阳盘','九宫顺逆排布，强调「值符随时干」；传统时家奇门主流、', ''),
('9bd234e9-fee4-4496-8791-9c7711552247','1698330600000', '1698330600000', NULL, 1, 0,'转盘阴盘','以‘月’为主，善理人事。', ''),
('f286520d-6193-4726-b991-8177e893401b','1698330600000', '1698330600000', NULL, 1, 0,'飞盘阳盘','跳跃式排星门，增加暗干与九神；复杂事件多维分析', ''),
('7416963e-435c-4bb4-81f0-f1d2c4773e76','1698330600000', '1698330600000', NULL, 1, 0,'飞盘鸣法','飞盘流派', ''),
('8187361a-77a1-46c3-9b5c-737ad20bb530','1698330600000', '1698330600000', NULL, 2, 0,'古法派','神煞格局、天地盘加临；《龙首经》《玉衡经》；古代祭祀、国运占卜', ''),
('c4fd56c0-ce19-4612-8b5b-0875a4f0cdc9','1698330600000', '1698330600000', NULL, 2, 0,'毕法派','九宗门起课、毕法赋口诀；《六壬断案》《毕法赋》；民事官司、财运婚姻', ''),
('e31e7019-8e48-4138-94f2-31af80a4a3cf','1698330600000', '1698330600000', NULL, 2, 0,'心镜派','心易直觉、类象直断；《大六壬心镜》；突发事件、灵感决策', ''),
('076da490-ff09-424c-97cd-3a6e0d7029a9','1698330600000', '1698330600000', NULL, 2, 0,'御定牌','天文分野、星象气候联动；《御定六壬直指》；军事战略、自然灾害预测', ''),
('aed54324-9211-4839-80eb-8f38c2a283cb','1698330600000', '1698330600000', NULL, 2, 0,'新式壬学','逻辑模型、能量流动分析；《六壬秘笈》《现代壬学》；商业投资、心理辅导', ''),
('03860eba-f216-4150-a3e5-f331936e0495','1698330600000', '1698330600000', NULL, 2, 0,'阴盘六壬','生肖年命一局多测；天地神三盘联动，重十神类象；弱化神煞强化神将；商业决策、团队运势。', '阴盘大六壬是传统大六壬的革新体系，以年命定位取代传统日干取用，融合天盘（动态取用）、地盘（静态方位）、神盘（心态与能量）三盘联动，弱化繁复神煞与九宗门，强调类象直断与一局多测（多人同步预测），引入十神断性格、伏吟转宫等技法，突出“神助大于一切”与应期精准，适用于商业决策、环境调理及灵异事件分析。');
